// To parse this JSON data, do////     final addPost = addPostFromJson(jsonString);import 'dart:convert';AddPost addPostFromJson(String str) => AddPost.fromJson(json.decode(str));String addPostToJson(AddPost data) => json.encode(data.toJson());class AddPost {  String title;  String body;  String userId;  int id;  AddPost({    this.title,    this.body,    this.userId,    this.id,  });  factory AddPost.fromJson(Map<String, dynamic> json) => AddPost(    title: json["title"],    body: json["body"],    userId: json["userId"],    id: json["id"],  );  Map<String, dynamic> toJson() => {    "title": title,    "body": body,    "userId": userId,    "id": id,  };}