// To parse this JSON data, do////     final allPost = allPostFromJson(jsonString);import 'dart:convert';List<AllPost> allPostFromJson(String str) => List<AllPost>.from(json.decode(str).map((x) => AllPost.fromJson(x)));String allPostToJson(List<AllPost> data) => json.encode(List<dynamic>.from(data.map((x) => x.toJson())));class AllPost {  int userId;  int id;  String title;  String body;  AllPost({    this.userId,    this.id,    this.title,    this.body,  });  factory AllPost.fromJson(Map<String, dynamic> json) => AllPost(    userId: json["userId"],    id: json["id"],    title: json["title"],    body: json["body"],  );  Map<String, dynamic> toJson() => {    "userId": userId,    "id": id,    "title": title,    "body": body,  };}