// To parse this JSON data, do////     final updatePost = updatePostFromJson(jsonString);import 'dart:convert';UpdatePost updatePostFromJson(String str) => UpdatePost.fromJson(json.decode(str));String updatePostToJson(UpdatePost data) => json.encode(data.toJson());class UpdatePost {  int id;  String title;  String body;  String userId;  UpdatePost({    this.id,    this.title,    this.body,    this.userId,  });  factory UpdatePost.fromJson(Map<String, dynamic> json) => UpdatePost(    id: json["id"],    title: json["title"],    body: json["body"],    userId: json["userId"],  );  Map<String, dynamic> toJson() => {    "id": id,    "title": title,    "body": body,    "userId": userId,  };}